            public void SendAndReceive(StringTree parameterizedRequest, out DateTime sentAt, out TimeSpan timeToLastByte, out string meta, out Exception exception) {
                _parameterizedRequest = parameterizedRequest;
                meta                  = "{\"type\":\"HTTP(s)\",\"requestContentLengthInKiB\":-1,\"responseContentLengthInKiB\":-1,\"encoding\":\"\",\"uncompressedTotalResponseLengthInKiB\":-1}";
                exception             = null;

                //
                // parameterizedRequest is the request parsed to a String Tree using the Scenario Rule Set.
                // String Tree is a simple class: it can have either a value (property: string Value) or childs who are also String Trees (e.g. StringTree foo = parameterizedRequest[n] where n is an integer value).
                // You can get the combined value of the childs using the function CombineValues() (not shallow, returns a string), this uses the child delimiters in the rule set to glue these values together.
                //

                // Owner not available in constructor, therefor set here.
                // This sets the owner name of the simulated user thread.
                // If the current thread is a 'parallel request thread' it's 'parent thread' owner name is returned.
                if(_parallelRequestThreadOwner == null){
                    _parallelRequestThreadOwner = Thread.CurrentThread.GetParent() as string;
                    if (_parallelRequestThreadOwner == null) {
                        _parallelRequestThreadOwner = Thread.CurrentThread.Name;
                    }
                }

                sentAt = HighResolutionDateTime.UtcNow;
                if (_isDisposed) {
                    timeToLastByte = new TimeSpan();
                    return;
                }
                try {
                    _stopwatch.Start();
                    try {
                        InitHttpWebRequest();
                        InitCookies();
                        HandleCookiesBeforeRequest();
                        ApplyPostData();
                        _httpWebResponse = _httpWebRequest.GetResponse() as HttpWebResponse;

                        double requestContentLengthInKiB = Convert.ToDouble(_httpWebRequest.ContentLength) / 1024;
                        if(requestContentLengthInKiB < 0) requestContentLengthInKiB = -1;

                        meta = "{\"type\":\"HTTP(s)\",\"requestContentLengthInKiB\":" + requestContentLengthInKiB.ToString().Replace(',', '.') + ",\"responseContentLengthInKiB\":"; //}

                        _httpWebResponse = _httpWebRequest.GetResponse() as HttpWebResponse;

                        double responseContentLengthInKiB = Convert.ToDouble(_httpWebResponse.ContentLength) / 1024;
                        if(responseContentLengthInKiB < 0) responseContentLengthInKiB = -1;
                        string encoding = _httpWebResponse.ContentEncoding.ToLower();

                        meta += responseContentLengthInKiB.ToString().Replace(',', '.') + ",\"encoding\":\"" + encoding + "\",\"uncompressedTotalResponseLengthInKiB\":";

                        if (encoding.Contains("gzip")) {
                            _streamReader = new StreamReader(new GZipStream(_httpWebResponse.GetResponseStream(), CompressionMode.Decompress));
                        }
                        else if(encoding.Contains("deflate")){
                            _streamReader = new StreamReader(new DeflateStream(_httpWebResponse.GetResponseStream(), CompressionMode.Decompress));
                        }
                        else {
                            _streamReader = new StreamReader(_httpWebResponse.GetResponseStream(), encoding.Length != 0 ?
                            System.Text.Encoding.GetEncoding(encoding) : System.Text.Encoding.GetEncoding(1252));
                        }

                        long length = 0L;

                        // EXAMPLE CUSTOM CP (Drupal): DATA EXTRACTION FROM RESPONSE
                        // IMPORTANT: be sure to
                        //      1) always execute DownloadResponse(out length)
                        //      2) execute it only once in every specific case
                        /*
                            string relUrl = _parameterizedRequest[(int)SyntaxItem.RelativeUrl].Value;
                            string method = _parameterizedRequest[(int)SyntaxItem.RequestMethod].Value;
                            string getData = _parameterizedRequest[(int)SyntaxItem.GetData].CombineValues();
                            string location = _httpWebResponse.GetResponseHeader("Location");

                            if (relUrl == "/node/add/article" && method == "GET") {
                                string response = DownloadResponse(out length, false); // Get full response in memory
                                string form_build_id = GetStringBetween(response, "<input type=\"hidden\" name=\"form_build_id\" value=\"", "\" />");
                                string form_token = GetStringBetween(response, "<input type=\"hidden\" name=\"form_token\" value=\"", "\" />");

                                _form_build_id.Set(_parallelRequestThreadOwner, form_build_id);
                                _form_token.Set(_parallelRequestThreadOwner, form_token);

                                #if DEBUGMODE
                                    EventPanel.AddEvent("form_build_id (ValueStore): " + _form_build_id.Get<string>(_parallelRequestThreadOwner));
                                    EventPanel.AddEvent("form_token (ValueStore): " + _form_token.Get<string>(_parallelRequestThreadOwner));
                                #endif
                            }
                            else {
                                DownloadResponse(out length); // Discard response while it gets downloaded
                            }

                            if (relUrl == "/node/add/article" && !string.IsNullOrEmpty(location)) {
                                _redirect_node.Set(_parallelRequestThreadOwner, location.Substring(location.IndexOf("/blog/", StringComparison.InvariantCultureIgnoreCase) + "/blog/".Length));

                                #if DEBUGMODE
                                    EventPanel.AddEvent("redirect_node: " + _redirect_node.Get<string>(_parallelRequestThreadOwner));
                                #endif
                            }
                        */

                        // Remove this instance of DownloadResponse(out length) if already used above this line for data extraction
                        DownloadResponse(out length);

                        //{
                        meta += ((double)length / 1024).ToString().Replace(',', '.') + "}";

                        HandleCookiesAfterRequest();
                    }
                    catch (Exception ex) {
                        exception = ex;
                        #if DEBUGMODE
                            EventPanel.AddEvent("SendAndReceive Exception: " + parameterizedRequest.CombineValues() + " " + exception);
                        #endif
                    }
                    finally {
                        if (_streamReader != null) {
                            try {
                                _streamReader.Close();
                                _streamReader.Dispose();
                            }
                            finally {
                                _streamReader = null;
                            }
                        }
                    }
                }
                catch {
                    //
                    // Always throw the exception, if any, if 'stuff' cannot be closed.
                    // vApus will handle it as a connection problem.
                    //

                    throw;
                }
                finally {
                    _stopwatch.Stop();
                    timeToLastByte = _stopwatch.Elapsed;
                    _stopwatch.Reset();
                }
            }

            public void Dispose() {
                if (!_isDisposed) {
                    _isDisposed = true;
                    try {
                        // Timeout immediately.
                        if(_httpWebRequest != null){
                            _httpWebRequest.Abort();
                        }
                    }
                    catch { }
                    _httpWebRequest = null;
                    _httpWebResponse = null;
                    _cookieCollection= null;
                    _cookieDecoder = null;
                    if (_streamReader != null) {
                        try {
                            _streamReader.Close();
                            _streamReader.Dispose();
                            _streamReader = null;
                        }
                        catch {
                        }
                    }
                }
            }
